import styled from 'styled-components';
import { Edit } from '@styled-icons/boxicons-solid';

export const Container = styled.div`
  align-self: center;
  margin-bottom: 30px;

  label {
    cursor: pointer;

    &:hover {
      opacity: 0.7;
    }

    img {
      height: 120px;
      width: 120px;
      border-radius: 50%;
      border: 3px solid rgba(255, 255, 255, 0.3);
      background: #eee;
    }

    input {
      display: none;
    }
  }
`;

export const Icon = styled(Edit)`
  position: relative;
  top: 0;
  color: #000;
`;
