import styled, { css } from 'styled-components';

export const Container = styled.div`
  align-self: center;
  margin-bottom: 30px;

  label {
    cursor: pointer;

    svg {
      ${css`
        &::after {
          position: absolute;
          left: 0;
          top: 0;
          width: 8px;
          height: 8px;
          background: #ff892e;
          border-radius: 50%;
        }
      `}}
    }

    &:hover {
      opacity: 0.7;
    }

    img {
      height: 120px;
      width: 120px;
      border-radius: 50%;
      border: 3px solid rgba(255, 255, 255, 0.3);
      background: #eee;
    }

    input {
      display: none;
    }
  }
`;
